mapper.insert:

	SELECT ATTACHMENT_SEQ.NEXTVAL FROM DUAL

INSERT INTO ATTACHMENT (
	ATTACH_NO
	, ATTACH_TYPE
	, ATTACH_NAME
	, ATTACH_SIZE
	, ATTACH_DATE
	, ATTACH_PATH
	, ATTACH_LOC
	, ATTACHMENT_GROUP_NO
) VALUES(
	#{attachNo}
	, #{attachType}
	, #{attachName}
	, #{attachSize}
	, sysdate
	, #{attachPath}
	, #{attachLoc}
	, #{attachmentGroupNo} 
)


	select EMP_NOTICE_SEQ.nextval from dual

insert into EMP_NOTICE (
	EMP_NOTICE_NO
	, EMP_NOTICE_TITLE
	, EMP_NOTICE_WRITER
	, EMP_NOTICE_DATE
	, EMP_NOTICE_CONTENT
	, EMP_NOTICE_FIX
	, ATTACHMENT_GROUP_NO
) values (
	#{empNoticeNo}
	, #{empNoticeTitle}
	, #{empNoticeWriter}
	, sysdate
	, #{empNoticeContent}
	, #{empNoticeFix}
	, #{attachmentGroupNo}
)

mapper.update:
update emp_notice
set
	EMP_NOTICE_WRITER = #{empNoticeWriter}
	, EMP_NOTICE_TITLE = #{empNoticeTitle}
	, EMP_NOTICE_CONTENT = #{empNoticeContent}
	, EMP_NOTICE_DATE = sysdate
	, EMP_NOTICE_FIX = #{empNoticeFix}
	, ATTACHMENT_GROUP_NO = ${attachmentGroupNo}
where EMP_NOTICE_NO = #{empNoticeNo}
