insert.foreach:
	INSERT INTO medicine_request (
       	medicine_request_no
  		,medicine_request_quantity
  		,MEDICINE_REQUEST_PERSON
  		,medicine_request_date
  		,mdcin_no
  		,medicine_request_unit
  	)
  	SELECT 
  		MEDICINE_REQUEST_SEQ.NEXTVAL
  		, #{item.mdcinQuantity}
  		, #{item.medicineRequestperson}
  		, sysdate
  		, #{item.mdcinNo}
  		, #{item.mdcinUnit}
  	FROM DUAL;

  	INSERT INTO medicine_request_result (
  		medicine_result_no, medicine_result_agree, medicine_request_no
  	) values (
  		MEDICINE_REQUEST_RESULT_SEQ.NEXTVAL, 'AS001', MEDICINE_REQUEST_SEQ.CURRVAL
  	)

mapper.update:
DECLARE
		v_success Number := 0;    
	BEGIN
		BEGIN
			UPDATE hospitalization_record hr
			SET hr.hsptlz_leave_date = sysdate, 
				hr.hsptlz_leave_reason = #{hsptlzRecordVO.hsptlzLeavereason}
			WHERE EXISTS (
				SELECT 1
				FROM "ORDER" o
				JOIN chart ch ON o.chart_no = ch.chart_no
				WHERE o.order_no = hr.order_no
				AND ch.chart_no = #{chartNo}
			);

			UPDATE bed
			SET
				bed_status = 'BS001'
			WHERE
				bed_no = #{bedVO.bedNo};

			UPDATE receipt
			SET 
				receipt_status = 'RS002'
			WHERE
				receipt_no = #{receiptNo};

			UPDATE history
			SET
				history_loc = 'LOC012'
			where
				receipt_no = #{receiptNo};

			v_success := 1;

		    IF v_success = 1 THEN
			   	 COMMIT;
			END IF;

			EXCEPTION
    			WHEN OTHERS THEN
    				v_success := 0;
        			ROLLBACK;
		END;	
	#{result, mode=OUT, jdbcType=INTEGER} := v_success;
END;

mapper.insert:
INSERT INTO pre_inspection (
    inspct_no
    , inspct_blood
    , inspct_elecgrm
    , inspct_urine
    , inspct_description
    , inspct_date
    , hsptlz_no
    , emp_no
) VALUES (
	PRE_INSPECTION_SEQ.NEXTVAL
	, #{inspctBlood}
	, #{inspctElecgrm}
	, #{inspctUrine}
	, #{inspctDescription}
	, sysdate
	, #{hsptlzNo}
	, #{empNo}
)

DECLARE
		v_success Number := 0;           
	BEGIN
		BEGIN
			INSERT INTO nursing_record (
				nursing_no
				, nursing_writer
				, nursing_date
				, nursing_content
				, hsptlz_no
				, patnt_no
				, receipt_no
			) VALUES (
			    nursing_record_seq.nextval
			    , #{empNo}
			    , sysdate
			    , #{nursingContent}
			    , #{hsptlzRecordVO.hsptlzNo}
			    , #{patntNo}
			    , #{receiptNo}
			);

			INSERT INTO io (
			    io_no,
			    io_intake,
			    io_emissions,
			    io_writer,
			    hsptlz_no,
			    io_date
			) VALUES (
			    io_seq.nextval
			    , #{ioVO.ioIntake}
			    , #{ioVO.ioEmissions}
			    , #{empNo}
			    , #{hsptlzRecordVO.hsptlzNo}
			    , sysdate
			);

			INSERT INTO vital (
			    vital_no,
			    pressure_max,
			    pressure_min,
			    vital_pulse,
			    respiration_min,
			    vital_heat,
			    hsptlz_no,
			    patnt_no,
			    vital_date,
			    emp_no
			) VALUES (
			    vital_seq.nextval
			    , #{vitalVO.pressureMax}
			    , #{vitalVO.pressureMin}
			    , #{vitalVO.vitalPulse}
			    , #{vitalVO.respirationMin}
			    , #{vitalVO.vitalHeat}
			    , #{hsptlzRecordVO.hsptlzNo}
			    , #{patntNo}
			    , sysdate
			    , #{empNo}
			);

	    	v_success := 1;

	    	IF v_success = 1 THEN
		   	 	COMMIT;
			END IF;
	EXCEPTION
	    WHEN OTHERS THEN
	        v_success := 0;
	        ROLLBACK;
	END;

	#{result, mode=OUT, jdbcType=INTEGER} := v_success;
END;

INSERT INTO nursing_record (
	nursing_no
	, nursing_writer
	, nursing_date
	, nursing_content
	, patnt_no
	, receipt_no
) VALUES (
	nursing_record_seq.nextval
	, #{nursingWriter}
	, sysdate
	, #{nursingContent}
	, #{patntNo}
	, #{receiptNo}
)
